import Navbar from '../components/Navbar';
import Footer from '../components/Footer';
import CTAButton from '../components/CTAButton';
import ServiceCard from "../components/ServiceCard";
import TeamProfile from "../components/TeamProfile";
import SlideShow from "../components/SlideShow";
import ConnectForm from "../components/ConnectForm";
import Curve from "../components/Curve";


export default function Index (){
    return (
        <div>
            <header>
                <Navbar />
                <div className="cta">
                    <h2 className ="tagline">Marketing Solutions to Move Your Brand Forward</h2>
                    <CTAButton link="/connect" >Connect</CTAButton>
                </div>
                
                <Curve />
            </header>            
            <main>
                
                <SlideShow />
                <ServiceCard link="/services" img="/icon1.png" caption="Social & Content Marketing" />
                <TeamProfile img="/userprofileimg1.jpg" alt="userimage" name="Jane Smith" title="CEO & Co Founder"></TeamProfile>
                <ConnectForm />
                
            </main>
            <Footer />


            <style jsx global>
                {`
                    body{
                        margin:0;
                        font-family: 'Gayathri', Helvetica, sans-serif;
                        box-sizing: border-box;
                        background-color: white;
                        overflow-x: hidden;
                        max-width: 100vw;
                        min-height: 100vh;
                    }

                    html{
                        scroll-behavior: smooth; /*currently only Firefox and Chrome */
                        font-size:10px;
                        overflow-x: hidden;
                        --main-color:#1B0DA5;
                        --accent-color:#FCFC42;
                        --secondary-color:#050037;
                        --bg-color: var(--main-color);
                        
                    }

                    header{
                        background-color: var(--main-color);
                        min-height: 80vh;
                        display:flex;
                        flex-direction:column;
                        position:relative;
                        border: 1px solid yellow;
                        color:white;
                    }

                    .cta {
                        display:flex;
                        flex-direction: column;
                        justify-self: flex-end;
                        place-self:flex-end;
                        align-items: end;
                        justify-content: flex-end;
                        padding: 1rem;
                        min-height: 30vh;
                        margin-bottom: 2rem;
                    }

                    .cta h2 {
                        width: 60%;
                        margin-left: 1rem;
                    }

                    main{
                        min-height: 50vh;
                        border:1px solid red;
                        background-color:var(--main-color);
                    }
                `}
            </style>
        </div>
    )
}
