    }

    render(){



        return(
            <div className="slideshow-container">
                <button className="prev-button" onClick={this.showPrev}></button>
                <div className="article-container">
                    <h2 className="article-title">Title</h2>
                    <img className="article-img" src="" alt="" />
                </div>
                
                <div className="indicators-container">
                    <div className="indicator" value={this.state.selectedSlideIndex} onClick={this.showSlide.bind(this, e)}></div>
                </div>

                <button className="next-button"></button>
                <style jsx>
                    {`
                        .indicator{
                            height:100px;
                            width: 100px;
                            background-color: gray;
                            border-radius:50%;
                            
                        }
                    `}
                </style>
            
            </div>
        )
    }
}
