import Link from "next/link";


export default function Card(props){
    return (
        <div className="card">
            {props.link ? <Link href={props.link}><a><figure>
                <img src={props.img} alt={props.alt}/>
                <figcaption>{props.caption}</figcaption>
            </figure></a></Link>:<figure>
                <img src={props.img} alt={props.alt}/>
                <figcaption>{props.caption}</figcaption>
            </figure>}
            <p> {props.img} </p>
            <img src= "../assets/icon1.png" />
            <style jsx>
                {`
                    .card{
                        border:1px solid var(--secondary-color);
                        border-radius:5px;
                        background:white;
                    }

                    figure{
                        display:grid;
                        grid-template-rows: 200px 2rem;
                        grid-template-areas:
                        "img",
                        "caption";

                    }

                    img{
                        grid-area: img;
                    }

                    figcaption{
                        font-weight:bold;
                        font-size:1.2rem;
                        color:var(--primary-color);
                        text-align:center;
                        grid-area: caption;
                    }
                `}
            </style>
            
        </div>
    )
}
