import Navbar from '../components/Navbar';
import Footer from '../components/Footer';
import CTAButton from '../components/CTAButton';
import ServiceCard from "../components/ServiceCard";
import TeamProfile from "../components/TeamProfile";
import SlideShow from "../components/SlideShow";
import ConnectForm from "../components/ConnectForm";
import Curve from "../components/Curve";


export default function Index (){
    return (
        <div>
            <header>
                <Navbar />
                <div className="cta">
                    <h2 className ="tagline">Marketing Solutions to Move Your Brand Forward</h2>
                    <CTAButton link="/connect" >Connect</CTAButton>
                </div>                
                <Curve />
            </header>            
            <main>
                <section>
                    <div className="blurb-container">
                        <h3 className="blurb-title">Lorem Ipsum Plubis -- Is Your Brand Ready?</h3>
                        <p className="blurb">Lorem ipsum dolor sit amet consectetur adipisicing elit. Itaque beatae blanditiis accusamus repellendus, accusantium perferendis repellat fugiat mollitia non fugit.</p>
                    </div>
                    <div className ="services-container">
                        <img className="bg-circle" src = "/bgcircle.svg" />
                        <ServiceCard link="/services" img="/icon1.png" caption="Branding & Strategy" />
                        <ServiceCard link="/services" img="/icon2.png" caption="Social & Content Marketing" />
                        <ServiceCard link="/services" img="/icon3.png" caption="Research & Data Analysis" />
                    </div>
                    
                </section>
                <section>
                    <div className="blurb-container">
                            <h3 className="blurb-title">Lorem Ipsum Plubis</h3>
                            <p className="blurb">Lorem ipsum dolor sit amet consectetur adipisicing elit. Itaque beatae blanditiis accusamus repellendus, accusantium perferendis repellat fugiat mollitia non fugit.</p>
                    </div>
                    <SlideShow />
                </section>
                <section className="bottom-section">
                    <div className="final-cta">
                        <h3>Lorem ipsum dolor sit amet, consectetur adipisicing elit. Itaque, explicabo!</h3>
                        <CTAButton link="/connect" >Connect</CTAButton>
                    </div>
                </section>
                
                
            </main>
            <Footer />


            <style jsx global>
                {`
                    body{
                        margin:0;
                        font-family: 'Gayathri', Helvetica, sans-serif;
                        box-sizing: border-box;
                        background-color: white;
                        overflow-x: hidden;
                        max-width: 100vw;
                        min-height: 100vh;
                    }

                    html{
                        scroll-behavior: smooth; /*currently only Firefox and Chrome */
                        font-size:10px;
                        overflow-x: hidden;
                        --main-color:#1B0DA5; 
                        --accent-color:#FCFC42;
                        --secondary-color:#050037;
                        --bg-color: var(--main-color);
                        
                    }

                    header{
                        background: var(--main-color) url("/herobg.jpg");
                        background-size:cover;
                        background-blend-mode:multiply;
                        min-height: 80vh;
                        display:flex;
                        flex-direction:column;
                        position:relative;
                        color:white;
                    }

                    .cta {
                        display:flex;
                        flex-direction: column;
                        align-self:flex-start;
                        justify-content: flex-end;
                        align-items:flex-start;
                        padding: 1rem;
                        min-height: 30vh;
                        margin-top:auto;
                        margin-bottom: 2rem;
                    }

                    .cta h2 {
                        width: 80%;
                        margin-left: 1rem;
                    }

                    main{
                        min-height: 100vh;
                        background:none;
                    }

                    section {
                        margin-top: 5rem;
                        display:flex;
                        flex-direction: column;
                        align-items: center;
                        justify-content: space-between;
                       
                    }

                    .blurb-container{
                        display: flex;
                        flex-direction: column;
                        justify-content: center;
                        align-items: center;
                        text-align:center;
                        color: var(--main-color);
                        margin: 2rem;
                        padding: 1rem;
                        min-height: 30vh;
                        font-size: 1.2rem;
                        width: 50%;
                    }
                    .services-container{
                        display:flex;
                        flex-wrap: wrap;
                        justify-content: space-around;
                        min-height: 50vh;
                        position: relative;

                    }

                    .bg-circle{
                        position: absolute;
                        left: 25%;
                        bottom: -40%;
                        height: 50%;
                        z-index: 20;
                    }
                    
                    .final-cta{
                        min-height: 50vh;
                        display: flex;
                        flex-direction: column;
                        align-items: center;
                        justify-content: center;
                        text-align:center;
                        color:black;
                        position:relative;
                        overflow:hidden;
                        background:none;
                        color:white;
                        width:100%;
                        
                    }

                    .final-cta h3 {
                        max-width: 70%;
                        margin-top:10%;
                    }

                    .final-cta h3::after{
                        content: " ";
                        position: absolute;
                        border-radius: 50%;
                        background-color: var(--main-color);
                        height: 70vh;
                        width: 120vw;
                        bottom: -60%;
                        left:-10%;
                        z-index: -1;

                    }

                    @media only screen and (min-width: 600px){
                        html{
                            font-size:20px;
                        }
                    }
                `}
            </style>
        </div>
    )
}
