import Link from "next/link";


export default function Card(props){
    return (
        <div className="card">
            {props.link ?<figure>
                <img src={props.img} alt={props.alt}/>
                <figcaption>{props.caption}</figcaption>
                <Link href="/services"><a className="learn-more" >Learn More</a></Link>
            </figure>
            :
            <figure>
                <img src={props.img} alt={props.alt}/>
                <figcaption>{props.caption}</figcaption>
            </figure>}
            <style jsx>
                {`
                    .card{
                        border:1px solid var(--secondary-color);
                        border-radius:5px;
                        background:white;
                        width:125px;
                        height:200px;
                        display:flex;
                        justify-content:center;
                        align-items:center;
                        box-shadow: 0 3px 6px #000;
                        margin: 1rem;
                    }

                    figure{
                        display:grid;
                        grid-template-rows: 70% 30%;
                        grid-template-areas:
                        "img"
                        "caption";
                        justify-items:center;
                        align-items:center;
                        padding:0;
                        margin:0;
                        width:100%;
                        height:100%;

                    }

                    img{
                        grid-area: img;
                        height:88px;
                        width:88px;
                    }

                    figcaption{
                        font-weight:bold;
                        font-size:1.2rem;
                        color:var(--main-color);
                        text-align:center;
                        grid-area: caption;
                        padding-top: .5rem;
                        align-self: start;
                    }

                    .learn-more{
                        color: var(--accent-color);
                        grid-row:1/3;
                        grid-column:1/2;
                        align-self:stretch;
                        justify-self:stretch;
                        text-align:center;
                        background-color:rgba(0, 0, 0, 0.8);
                        text-decoration: none;
                        display:flex;
                        align-items:center;
                        justify-content: center;
                        font-size: 1.5rem;
                        opacity: 0;
                        transition: opacity 0.3s ease;
                    }

                    .learn-more:hover{
                        opacity: 1;
                    }

                    

                `}
            </style>
            
        </div>
    )
}
