import { Component } from "react";

export default class SlideShow extends Component{
    constructor(props){
        super(props);
        this.state = {
            slides: [{id: 0, heading:"Case Study 1", image:"/img1.jpg"}, {id: 1, heading:"Case Study 2", image:"/img2.jpg"}, {id: 2, heading:"Case Study 3", image:"/img3.jpg"}],
            selectedSlideIndex: 0
        }
        this.showNext = this.showNext.bind(this);
        this.showPrev = this.showPrev.bind(this);
        this.showSlide = this.showSlide.bind(this);
    }


    showPrev(e){
        e.preventDefault();
        const slidesLength = this.state.slides.length;

    }

    showNext(e){

    }

    showSlide(e){
        console.log("e.target:", e.target.attributes.index)
    }

    render(){



        return(
            <div className="slideshow-container">
                <button className="prev-button" onClick={this.showPrev}></button>
                {this.state.slides.forEach((slide, index) => {
                    return(
                    <div className={"article-container" + this.state.selectedSlideIndex == index ? " active" : ""} key={index} >
                        <h2 className="article-title">{this.state.slides[index].heading} </h2>
                        <img className="article-img" src={this.state.slides[index].image} alt="" />
                    </div>
                    )
                })}
                
                
                <button className="next-button"></button>
                <style jsx>
                    {`
                        .indicator{
                            height:100px;
                            width: 100px;
                            background-color: gray;
                            border-radius:50%;
                            
                        }
                    `}
                </style>
            
            </div>
        )
    }
}
